$(document).ready(function() {
	$(".form").validator({
		timely : 3,
		fields : {
			mail : "email",
			telephone : "required; mobile",
			username : "required;length(4~10)",
			password : "required;length(6~16)",
			password1 : "required;match(password);length(6~16)",
			verify : "required"
		},
		valid: function(form) {
			$.post("userRegister", $(form).serialize()).done(function(d) {
				if(d.code != -1 && d.code != -2){
					window.location.href="frontLogin";
				}else{
					layer.alert(d.msg, {icon : 5})
				}
			});
		}
	})
});